      out.write("\t\t\t\t\t\t\tfunction calculateMA(dayCount) {\r\n");
      out.write("\t\t\t\t\t\t\t    var result = [];\r\n");
      out.write("\t\t\t\t\t\t\t    for (var i = 0, len = data0.values.length; i < len; i++) {\r\n");
      out.write("\t\t\t\t\t\t\t        if (i < dayCount) {\r\n");
      out.write("\t\t\t\t\t\t\t            result.push('-');\r\n");
      out.write("\t\t\t\t\t\t\t            continue;\r\n");
      out.write("\t\t\t\t\t\t\t        }\r\n");
      out.write("\t\t\t\t\t\t\t        var sum = 0;\r\n");
      out.write("\t\t\t\t\t\t\t        for (var j = 0; j < dayCount; j++) {\r\n");
      out.write("\t\t\t\t\t\t\t            sum += data0.values[i - j][1];\r\n");
      out.write("\t\t\t\t\t\t\t        }\r\n");
      out.write("\t\t\t\t\t\t\t        result.push(sum / dayCount);\r\n");
      out.write("\t\t\t\t\t\t\t    }\r\n");
      out.write("\t\t\t\t\t\t\t    return result;\r\n");
      out.write("\t\t\t\t\t\t\t}\r\n");
